import React, { Component } from 'react'
// kết nối giỏ hàng redux(connect hàm kết nối reactComponent với redux store )
import{connect} from'react-redux'

class ModalGioHangRedux extends Component {

renderGioHangRedux  = () => {
    // this.props.gioHang la thuộc tính nhận từ redux
    return this.props.gioHang.map (  (spGH, index) => {
        return <tr key={index}>
            <td></td>
        </tr>
    } ) 
}

  render() {
    return (
      <div className='container'>
        <table className='table'>
<thead>
    <tr>
        <th>Mã sản phẩm</th>
        <th>Tên sản phẩm</th>
        <th>Số lượng</th>
        <th>Giá bán</th>
    </tr>
</thead>
        </table>

      </div>
    )
  }
}
const mapStateToProps   = (state) => {  // state là store tổng => truy xuất đến GioHangReducers => biến state trên GioHangReducers
    return {
        gioHang:state.GioHangReducers.gioHang  // tạo ra 1 prop giỏ hàng của ModalGioHangRedux
    }
}
export default connect(mapStateToProps,null)(ModalGioHangRedux)
