import React, { Component } from "react";
import ModalGioHang from "./ModalGioHang";
import DanhsachSanPhamGioHang from "./DanhsachSanPhamGioHang";
import phoneData from "../../data/phoneData.json";

export default class BaiTapGioHang extends Component {
  constructor(props) {
    super(props);
    this.state = {
      gioHang: [
        {
          maSP: 1,
          tenSP: "VinSmart Live",
          manHinh: "AMOLED, 6.2, Full HD+",
          giaBan: 5700000,
          hinhAnh: "./img/vsphone.jpg",
          soLuong: "1",
        },
      ],
    };
  }

  //   Lấy dữ liệu tại component BaiTapGioHang
  themGioHang = (sanPhamChon) => {
    console.log(sanPhamChon);
   

    this.setState = {
      gioHang: [...this.state.gioHang, sanPhamChon],
    };
  };

  render() {
    let tongSoLuong = this.state.gioHang.reduce((tongSoLuong, spGh, index) => {
      return (tongSoLuong += spGh.soLuong);
    }, 0);
    return (
      <div className="container">
        <h2 className="text-center text-success mt-3 ">Bài Tập Giỏ Hàng</h2>
        <ModalGioHang gioHang={this.state.gioHang} />
        <div className=" mb-3 mr-3 text-right">
          <button
            className="btn btn-success"
            data-toggle="modal"
            data-target="#modelId"
          >
            {" "}
            Giỏ Hàng ({tongSoLuong})
          </button>{" "}
        </div>
        <DanhsachSanPhamGioHang
          themGioHang={this.themGioHang}
          mangSanPham={phoneData}
        />
      </div>
    );
  }
}
