import React, { Component } from "react";
import phoneData from "../../data/DataPhone.json";

export default class DanhSachSanPham extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sanPhamChiTiet: {},
    };
  }

  renderPhone = () => {
    // lấy props mảng sp từ component ngoài vào
    return phoneData.map((phone, index) => {
      return (
        <div className="card col-4" key={index}>
          <img className="card-img-top" src={phone.hinhAnh} alt />
          <div className="card-body">
            <h4 className="card-title">Title</h4>
            <p className="card-text">Text</p>
          </div>
        </div>
      );
    });
  };
  render() {
    return (
      <div className="container">
        <div className="row">{this.renderPhone()}</div>
      </div>
    );
  }
}
