import React, { Component } from 'react'
import data from '../data/DataFilms.json'

export default class BaiTapVongLap extends Component {
    constructor(props) {
        super(props)
        this.state={
            mangPhim:data
        }  
    }

    renderMangPhim=()=>{
        return this.state.mangPhim.map((phim,index)=>{
            return <div className="card col-4">
  <img className="card-img-top" src={this.state.mangPhim.hinhAnh} alt />
  <div className="card-body">
    <h4 className="card-title">{this.state.mangPhim.tenPhim}</h4>
    <p className="card-text">{this.state.mangPhim.mo}</p>
  </div>
</div>

        })

        }
   


  render() {
    console.log(this.state.mangPhim)
    return (
      <div className='container'>
        <div className='row'>
            {this.renderMangPhim()}
        </div>
      </div>
    )
  }
}
